package LearningJava;
class Demo1 extends Thread
{
	public void run()
	{
		System.out.println("");
		
		for(int i=1;i<=5;i++)
		{
			System.out.println("");
			
			try
			{
				Thread.sleep(1000);
			}
			catch(Exception e)
			{
				System.out.println("");
			}
		}
		System.out.println("");
	}
}
